mapper.select:
select id, wb_id, name, consignor_name, consignor_phone, packing, number, weight, freight, deliveryfee, pickupfee, damages, dept, wb_status, recordingtime, remarks
from waybill where company_id=#{company_id} and wb_status=#{wb_status}
and name=#{name}
and wb_id=#{wb_id}
limit #{start},#{pageSize}

mapper.insert:
insert into waybill ( wb_id, name, consignee_name, consignee_phone, consignor_name, consignor_phone, packing, number, weight, freight, deliveryfee, pickupfee, damages, dept, wb_status, recordingtime, remarks, company_id
) values ( #{wb_id}, #{name}, #{consignee_name}, #{consignee_phone}, #{consignor_name}, #{consignor_phone}, #{packing}, #{number}, #{weight}, #{freight}, #{deliveryfee}, #{pickupfee}, #{damages}, #{dept}, #{wb_status}, #{recordingtime}, #{remarks}, #{company_id}
)

mapper.update:
update
     waybill set
                wb_status=#{wb_status}  where id=#{id}
